mapper.select:
SELECT sum(money) AS amount
FROM t_cost_info
WHERE inOutType = #{inOutType,jdbcType=VARCHAR} AND userId=#{userId,jdbcType=BIGINT}
AND createTime LIKE CONCAT(#{month,jdbcType=VARCHAR}, '%')

    AND isHidden = #{isHidden,jdbcType=INTEGER}


    AND isDelete = #{isDeleted,jdbcType=INTEGER}
