FROM t_liability


        and id=#{id,jdbcType=BIGINT}


        and user_id=#{userId,jdbcType=BIGINT}


        and create_time=#{createTime,jdbcType=TIMESTAMP}


        and modify_time=#{modifyTime,jdbcType=TIMESTAMP}


        and repayment_day=#{repaymentDay,jdbcType=TIMESTAMP}


        and name=#{name,jdbcType=VARCHAR}


        and type=#{type,jdbcType=VARCHAR}


        and parent_type=#{parentType,jdbcType=VARCHAR}


        and amount=#{amount,jdbcType=BIGINT}


        and installment=#{installment,jdbcType=INTEGER}


        and index=#{index,jdbcType=INTEGER}


        and status=#{status,jdbcType=INTEGER}


        and paid=#{paid,jdbcType=BIGINT}


ORDER BY

    ${orderBy} ${sort}


    id ${sort}

LIMIT #{index} , #{offset}

